import React from "react";
import { Layout, Image } from 'antd';

const { Content } = Layout;

const Introduce = () => {
    return (
        <Layout style={{ backgroundColor: '#01050B', position: 'relative', height: '100vh' }}>
            <Content style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', position: 'relative', height: '100%' }}>
                <Image
                    preview={false}
                    alt="img"
                    src="/resources/images/introduce.png"
                    className="responsive-image"
                    style={{
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        right: 0,
                        bottom: 0,
                        width: '100%',
                        height: '100%',
                        objectFit: 'cover' // 确保图片覆盖整个区域
                    }}
                />
            </Content>
        </Layout>
    );
};

export default Introduce;
