+    if (newMintsCreated.length > 0) {
+      setMintsCreated(newMintsCreated);
+      onOpen();
+    }
+  } catch (e) {
+    console.error(`minting failed because of ${e}`);
+    createStandaloneToast().toast({
+      title: "Your mint failed!",
+      description: "Please try again.",
+      status: "error",
+      duration: 900,
+      isClosable: true,
