+        d{" "}
+        {hours.toLocaleString("en-US", {
+          minimumIntegerDigits: 2,
+          useGrouping: false,
+        })}
+        h{" "}
+        {minutes.toLocaleString("en-US", {
+          minimumIntegerDigits: 2,
+          useGrouping: false,
+        })}
+        m{" "}
+        {seconds.toLocaleString("en-US", {
+          minimumIntegerDigits: 2,
+          useGrouping: false,
+        })}
+        s
+      </Text>
+    );
+  }
+  if (hours > BigInt(0)) {
+    return (
+      <Text fontSize="sm" fontWeight="bold">
+        {hours.toLocaleString("en-US", {
+          minimumIntegerDigits: 2,
+          useGrouping: false,
+        })}
+        h{" "}
+        {minutes.toLocaleString("en-US", {
+          minimumIntegerDigits: 2,
+          useGrouping: false,
+        })}
+        m{" "}
+        {seconds.toLocaleString("en-US", {
+          minimumIntegerDigits: 2,
+          useGrouping: false,
+        })}
+        s
+      </Text>
+    );
+  }
+  if (minutes > BigInt(0) || seconds > BigInt(0)) {
+    return (
+      <Text fontSize="sm" fontWeight="bold">
+        {minutes.toLocaleString("en-US", {
+          minimumIntegerDigits: 2,
+          useGrouping: false,
+        })}
+        m{" "}
