-// new component called timer that calculates the remaining Time based on the bigint solana time and the bigint toTime difference.
-const Timer = ({
-  solanaTime,
-  toTime,
-  setCheckEligibility,
-}: {
-  solanaTime: bigint;
-  toTime: bigint;
-  setCheckEligibility: Dispatch<SetStateAction<boolean>>;
