+      candyGuard,
+      nftMint,
+      guardToUse,
+      mintArgs,
+      tables,
+      latestBlockhash,
+      1_400_000,
+      buyBeer
+    );
+    const requiredCu = await getRequiredCU(umi, txForSimulation);
+
+    for (let i = 0; i < mintAmount; i++) {
