+    [label: string]: number;
+  }>({});
+  if (!candyMachine || !candyGuard) {
+    return <></>;
+  }
+
+  const handleNumberInputChange = (label: string, value: number) => {
+    setNumberInputValues((prev) => ({ ...prev, [label]: value }));
